es->select_all_where(2);      $data['subview'] = 'admin/cars/crashes_index';      $this->load->view('admin_index', $data);      }    public function crash_status($status,$id){      $this->load->model('cars/Car_crashes');      $this->Car_crashes->update_status($status,$id);      redirect('dashboard/crashes_index');      }    public function R_drivers(){      $this->load->model('cars/Driver');      $data['table']=$this->Driver->select();      $data['subview'] = 'admin/cars/R_drivers';      $this->load->view('admin_index', $data);      }    public function R_driver_status(){      $this->load->model('cars/Car_crashes');      $data['cars'] = $this->Car_crashes->select_cars();      $data['records']=$this->Car_crashes->select_drivers();      $data['table']=$data['records'][0];      $data['order'] = $this->Car_crashes->unordered_cars();      $data['subview'] = 'admin/cars/R_driver_status';      $this->load->view('admin_index', $data);      }    public function R_violation(){      $this->load->model('cars/Car_crashes');      $data['cars'] = $this->Car_crashes->select_cars();      if($this->input->post('date_from') && $this->input->post('date_to') && $this->input->post('car')){        $data['records']=$this->Car_crashes->select_drivers();        $data['drivers']=$data['records'][1];        $data['table'] = $this->Car_crashes->select_violation(strtotime($this->input->post('date_from')),strtotime($this->input->post('date_to')),$this->input->post('car'));        $this->load->view('admin/cars/load_violation',$data);      }      else{          $data['subview'] = 'admin/cars/R_violation';          $this->load->view('admin_index', $data);      }      }    public function R_cars(){      $this->load->model('cars/Car_crashes');      $data['table']=$this->Car_crashes->R_cars();      $data['subview'] = 'admin/cars/R_cars';      $this->load->view('admin_index', $data);      }    public function home_cars(){      $data['subview'] = '